$(document).ready(function(){$(document.body).append("<span id=\"comment-respond\">Respond</span>");var j=$('#comment-respond');j.css('position','absolute');j.fadeTo(10,0);var k="";var l="";var m="";$('.commentlist > .comment, .quotable').each(function(){$(this).mouseup(function(e){j.css('top',e.pageY+10);j.css('left',e.pageX+10);if(getSelText()){j.show();j.fadeTo("normal",0.3);var a=findCommentFor(e.target);if(a){k=findPermalinkFor(a).href;l=findAuthorFor(a).text}else{k=false;l=false}m=getSelText()}})});j.hover(function(){$(this).fadeTo("fast",1)},function(){$(this).fadeTo("fast",0.3)});j.mousedown(function(e){var a="";if(k&&l){a="<p><a href=\""+k+"\">@"+l+"</a>:</p>\n"}$('#comment').val(a+"<blockquote>"+m+"</blockquote>\n<p>\n<!-- Start your comment below this line. -->\n\n</p>");$.scrollTo('#comment',{duration:1000});$(this).fadeTo(1,0);j.hide()});$(document.body).mousedown(function(){j.hide()});$('.commentlist .comment a').click(function(e){var a=getAnchor(this.href);if($('.commentlist '+a).length>0){setCurrentComment(a);$.scrollTo(a,{duration:1000});return false}});var n=$($('.commentlist').get(0));var o=new Array();var p=new Array();var q=n.hasClass('sorted-commentary');$('.commentlist .comment p:first-child a:first-child').each(function(){if(this.text.substring(1,-1)=="@"){var a=this.text.substring(1,this.text.length);var b=findCommentFor(this);var c=findAuthorFor(b);var d=findPermalinkFor(b);var e=getAnchor(this.href);var f='<a href="'+d.href+'">'+c.innerHTML+'</a>';var g=$('.commentlist #'+e.substr(1,e.length));if(g.length>0&&q){$(g.get(0)).after(b.addClass('response'))}if(!p[e]){p[e]=new Array(f)}else{p[e][p[e].length]=f}}});var r='';if(!n.hasClass('no-quote-control')){r+='<a href="#" class="comment-quote">Quote</a>'}if(!n.hasClass('no-reply-control')){r+='<a href="#" class="comment-reply">Reply</a>'}$('.commentlist > .comment').each(function(){var c=findAuthorFor(this);var d=findPermalinkFor(this);var f=getAnchor(d.href);var g='<a href="'+d.href+'">'+d.innerHTML+'</a>';if(!o[c.text]){o[c.text]=new Array(g)}else{o[c.text][o[c.text].length]=g}$(this).append('<div class="comment-controls">'+r+'</div>');$(this).find('.comment-reply, .comment-quote').each(function(){if($(this).hasClass('comment-reply')){$(this).click(function(e){setupComment(this,false);return false})}if($(this).hasClass('comment-quote')){$(this).click(function(e){setupComment(this,true);return false})}});var h=false;var i=$($(this).find('.comment-controls'));i.hide();$(this).hover(function(){if(o[c.text].length>1||p[f]){if($(this).find('div.comment-controls div.related-replies, div.comment-controls div.related-comments').length<1){if(p[f]&&!n.hasClass('no-replies')){var b="";(p[f].length>1)?b="replies":b="reply";i.append('<div class="related-replies"><h6>'+(p[f].length)+' '+b+' to this comment</h6><ol>'+printReplies(p[f])+'</ol></div>')}if(o[c.text].length>1&&!n.hasClass('no-relatives')){var b="";(o[c.text].length-1>1)?b="comments":b="comment";i.append('<div class="related-comments"><h6>'+(o[c.text].length-1)+' other '+b+' from '+c.text+'</h6><ol>'+printRelatives(o[c.text],g)+'</ol></div>')}$(this).find('div.comment-controls ol li a').click(function(e){var a=getAnchor(this.href);setCurrentComment(a);$.scrollTo(a,{duration:1000});return false})}}if(!i.is(':visible')&&!h){h=setTimeout(function(){i.fadeIn("fast");h=false},300)}else{clearTimeout(h);h=false}},function(){if(!i.is(':visible')&&h){clearTimeout(h);h=false}else if(i.is(':visible')&&!h){h=setTimeout(function(){i.fadeOut("fast");h=false},500)}})})});function findCommentFor(a){a=$(a);while(!a.hasClass('comment')){if(a.hasClass('quotable')){return false}a=$(a.parent())}return a};function findPermalinkFor(a){return $(a).find('.comment-permalink').get(0)};function findAuthorFor(a){return $(a).find('.comment-author-name').get(0)};function printRelatives(a,b){var c="";var d="";for(var i=0;i<=a.length-1;i++){(a[i]==b)?d="current":d="";c+='<li class="'+d+'">'+a[i]+'</li>'};return c};function printReplies(a){var b="";for(var i=0;i<=a.length-1;i++){b+='<li>'+a[i]+'</li>'};return b};function setCurrentComment(a){$('.commentlist .current-comment').removeClass('current-comment');$($('.commentlist '+a).get(0)).addClass('current-comment')};function getAnchor(a){return'#'+a.split("#")[1]};function setupComment(b,c){var d=findCommentFor(b);var e='<p><a href="'+findPermalinkFor(d).href+'">@'+findAuthorFor(d).text+'</a></p>';if(c){c="<blockquote>";if(d.find('.entry-content > p').length>0){d.find('.entry-content > p').each(function(){var a=false;if($(this).find("a:first-child").length>0){if($(this).find("a:first-child").get(0).text.substring(1,-1)=="@"){a=true}}if(!a){c+="<p>"+this.innerHTML+"</p>"}})}else{c=d.innerHTML}c+="</blockquote>";c=c.replace("\n<!-- Start your comment below this line. -->\n\n","");c=c.replace("\n<!-- Start your comment below this line. -->\n","");c=c.replace("<!-- Start your comment below this line. -->","")}else{c=""}$('#comment').val(e+c+"\n<p>\n<!-- Start your comment below this line. -->\n\n</p>");$.scrollTo('#comment',{duration:1000})};function getSelText(){var a='';if(window.getSelection){a=window.getSelection()}else if(document.getSelection){a=document.getSelection()}else if(document.selection){a=document.selection.createRange().text}else return;if(String(a).length>2){a=String(a).replace(/\n\n/g,"</p><p>").replace(/\n/g,"<br/>");return"<p>"+a+"</p>"}else{return false}};